        checkout.open({
            title: 'wpeazy-ai',
            licenses: currentLicenseValue,
            billing_cycle: 'lifetime',
            success: function () {},
            purchaseCompleted: function (response) {
                var isTrial = response.purchase.trial_ends != null,
                    isSubscription = response.purchase.initial_amount != null,
                    total = isTrial ? 0 :
                           (isSubscription ? response.purchase.initial_amount : response.purchase.gross).toString(),
