                    storeUrl    = 'https://spider-themes.net/wpeazyai',
                    storeName   = 'Spider Themes';

                if (typeof gtag !== 'undefined') {
                    gtag('event', 'purchase', {
                        transaction_id: response.purchase.id.toString(),
                        affiliation: storeName,
                        value: total,
                        shipping: 0,
                        tax: 0,
                        currency: response.purchase.currency.toUpperCase(),
                        items: [{
                            item_id: response.purchase.plugin_id.toString(),
                            item_variant: response.purchase.plan_id.toString(),
                            item_name: productName,
                            item_category: 'Plugin',
                            price: total,
                            quantity: 1,
                            currency: response.purchase.currency.toUpperCase()
                        }]
                    });

                    gtag('event', 'page_view', {
                        page_title: '/purchase-completed/',
                        page_location: storeUrl + '/purchase-completed/',
                    });
                }
            }
        });
			});
